mediaDisplay.onEnter:
setParentFade(128);

print("@@@@@@@@@@@@@@@@@@@ into popup ");
	print("header = ", header);
	print("message = ", message);
	print("btnSize = ", btnSize);
	print("btnString = ", btnString);

mediaDisplay.onUserInput:
handled = "false";
userInput = currentUserInput();
currentItem = getFocusItemIndex();
print("@@@@@@ get input=",userInput);
print("@@@@@@ get curren item=",currentItem);	
if ("enter" == userInput) {
	data = getStringArrayAt(btnString, currentItem);
	setReturnString(data);
	postMessage("return");
	handled = "true";
}
handled;

image.script:
icon = "../image/Confirm_focus.png";
if (btnSize == 1) {
	icon = "../image/Confirm_focus.png";
}
else {
	index = getQueryItemIndex();
	focus = getFocusItemIndex();
	if (index == focus) {
		icon = "../image/Confirm_focus.png";
	}
	else {
		icon = "../image/Confirm_unfocus.png";
	}
}
icon;

foregroundColor.script:
fgcolor = "255:255:255";
index = getQueryItemIndex();
focus = getFocusItemIndex();
if (index == focus) {
	fgcolor = "255:255:255";
}
else {
	fgcolor = "255:255:255";
}
fgcolor;

text.script:
index = getQueryItemIndex();
data = getStringArrayAt(btnString, index);
data;
