if( userInput == "one")
{
	size = "3x4";
	showIdle();
	redrawDisplay();
	ret = "true";
}
if( userInput == "two")
{
	size = "4x6";
	showIdle();
	redrawDisplay();
	ret = "true";
}
if( userInput == "enter")
{
	immagine =getStringArrayAt(titleArray, getFocusItemIndex());
	arr = pushBackStringArray(arr, immagine);
	writeStringToFile(paramFile, arr);
	arr = null;
	showIdle();
	if (size ==	"4x6")
		jumpToLink("destination4x6");
	else
		jumpToLink("destination3x4");
	ret = "true";
}
