rss.onEnter:
paramFile = "scripts/Juegos/xPuzzle/parameters.dat";
data = readStringFromFile(paramFile);
sel = getStringArrayAt(data, 0);
rows = 4;
cols= 6;
cells = 24;
size="4x6";	
rowsM1 = minus(rows,1);
curIdx = 0; 
nextIdx = curIdx;
low = minus(cells , rows);
high = minus(cells , 1);
displaySolution = "false";
solved = "false";
redrawDisplay();

mediaDisplay.onUserInput:
ret = "false";
userInput = currentUserInput();
if( solved == "false")
{		
	if( userInput == "right")		
	{
		if (curIdx >= rows)
		{
			nextIdx = minus(curIdx,rows);
			executeScript("move");
			redrawDisplay();
			ret = "true";
		}
	}

	if( userInput == "left")		
	{
		if ((curIdx >= low) && (curIdx < high))
			ret = "true";
		else if (curIdx < low)
		{
			nextIdx = add(curIdx,rows);
			executeScript("move");
			redrawDisplay();
			ret = "true";
		}
	}


	if( userInput == "down")		
	{
		modulo = Integer(curIdx%rows);
		if(modulo > 0)
		{
			nextIdx = minus(curIdx,1);
			executeScript("move");
			redrawDisplay();
			ret = "true";
		}
	}


	if( userInput == "up")		
	{
		modulo = Integer(curIdx%rows);
		if(modulo < rowsM1)
		{
			nextIdx = add(curIdx,1);
			executeScript("move");
			redrawDisplay();
			ret = "true";
		}
	}

	if (( userInput == "enter" ) || (userInput == "display"))		
	{
		displaySolution = (displaySolution == "false") ? "true" : "false";
		redrawDisplay();
		ret = "true";
	}
}
ret;

itemDisplay.script:
itemIdx = getQueryItemIndex();
if (displaySolution == "false")
	title = getStringArrayAt(scrambleArray,itemIdx);
else
	title = getStringArrayAt(titleArray,itemIdx);

image = "scripts/Juegos/xPuzzle/images/"+sel+"4x6/" + title + ".jpg";
