rss.onEnter:
	showIdle();
	textIdx = 0;
	nbText = 4;
	text0 = "Selecciona difucltad: 
1 - Facil, 4 - Dificil.
Mueve el cursor pulsando las flechas";
	text1 = "Selecciona el numero pulsando en el mando 1, 2, 3, 4, 5, 6, 7, 8 y 9";
	text2 = "Para borrar un numero, pulsa '0'";
	text3 = "Para comprobar los errores, pulsa 'OK'";
	setRefreshTime(5000);
	redrawDisplay();
	sHttpPath="http://iamm.xf.cz/XGames/xSudoku/";

rss.onRefresh:
textIdx = add(textIdx,1);
if (textIdx >= nbText) 
	textIdx = 0;
redrawDisplay();

mediaDisplay.onUserInput:

ret = "false";	
userInput = currentUserInput();
if( userInput == "one")	
{		
	setRefreshTime(-1);		
	url = sHttpPath + "xSudoku1.php?level=1";		
	showIdle();		
	jumpToLink("destination");		
	ret = "true";	
}
if( userInput == "two")	
{		
	setRefreshTime(-1);		
	url = sHttpPath + "xSudoku1.php?level=2";
	showIdle();		
	jumpToLink("destination");		
	ret = "true";	
}	
if( userInput == "three")	
{		
	setRefreshTime(-1);		
	url = sHttpPath + "xSudoku1.php?level=3";
	showIdle();		
	jumpToLink("destination");		
	ret = "true";	
}	
if( userInput == "four")	
{
	setRefreshTime(-1);		
	url = sHttpPath + "xSudoku1.php?level=4";
	showIdle();		
	jumpToLink("destination");		
	ret = "true";	
}
ret;

text.script:
if(textIdx == 0) 
	text0;
else if(textIdx == 1) 
	text1;
else if(textIdx == 2) 
	text2;
else if(textIdx == 3) 
	text3;
else "";
