rss.onEnter:
  
userName = readStringFromFile("/usr/local/etc/dvdplayer/woxter_name.dat");
  
passWord = readStringFromFile("/usr/local/etc/dvdplayer/woxter_passwd.dat");
  
titleString = "Login woxtermusic";
  
print("userName",userName);
  
print("passWord",passWord);

title.script:
			
displayName = "Enter Username";
			
if ( userName != null)
			
{
			   displayName = userName;
			}
			
displayName;

item.onClick:
		
inputUser = getInput("userName", "doModal");
		
if (inputUser != null)
		
{
		   userName = inputUser;
		}
		
redrawDisplay();

title.script:
			
displayTitle = "Enter Password";
			
if ( passWord != null)
			
{
			   displayTitle = "****************";
			}
			
displayTitle;

item.onClick:
		
inputPass = getInput("passWord", "doModal");
		
if (inputPass != NULL)
		
{
		   passWord = inputPass;
		}
		
redrawDisplay();

		
if (passWord == null || userName == null)
		
{
			
titleString = "Invalid username or password";
			
redrawDisplay();
		
}
		
else
		
{

                      
writeStringToFile("/usr/local/etc/dvdplayer/woxter_name.dat", userName);
                      
writeStringToFile("/usr/local/etc/dvdplayer/woxter_passwd.dat", passWord);
                      
"music.rss";
		}
