onUserInput.script:
ret = "false";

userInput = currentUserInput();
majorContext = getPageInfo("majorContext");

print("*** majorContext=",majorContext);
print("*** userInput=",userInput);

if (userInput == "pagedown" || userInput == "pageup" || userInput == "PG" || userInput == "PD")
{
  idx = Integer(getFocusItemIndex());
  if (userInput == "pagedown" || userInput == "PD")
  {
    idx -= -5;
    if(idx >= itemSize)
      idx = itemSize-1;
  }
  else
  {
    idx -= 5;
    if(idx < 0)
      idx = 0;
  }
  setFocusItemIndex(idx);
	setItemFocus(idx);
  redrawDisplay();
  ret = "true";
}
else if(majorContext == "items" && (userInput == "right" || userInput == "R"))
{
  idx = getFocusItemIndex();
  url = getStringArrayAt(urlArray, idx);
  title = getStringArrayAt(titleArray, idx);
  thumbnail = getStringArrayAt(thumbnailArray, idx);
  description = getXMLText("rss","channel","item",idx,"description");

  arr = null;
  arr = pushBackStringArray(arr, url);
  arr = pushBackStringArray(arr, title);
  arr = pushBackStringArray(arr, thumbnail);
  writeStringToFile(storagePath + "entry.dat", arr);

  arr = null;
  arr = pushBackStringArray(arr, "<body>");
  arr = pushBackStringArray(arr, description);
  arr = pushBackStringArray(arr, "</body>");
  writeStringToFile(storagePath + "entry.htm", arr);

  print("description",description);

  act = doModalRss(translate_base_url + "app/ivi/index,entry");
  ret = "true";
}
ret;

rss.onEnter:
  cachePath = getStoragePath("key");



	pageLogo = "../etc/translate/rss/image/menu/ivi.png";

  storagePath = getStoragePath("tmp");
  arr = readStringFromFile(storagePath + "stream.dat");
  feedPath = getStringArrayAt(arr, 0);
  feedTitle = getStringArrayAt(arr, 1);

	feedType = "new";

  mode = 1;
  setRefreshTime(1);

rss.onRefresh:
  if(mode == 1)
  {
    setRefreshTime(-1);

  	pageTitle = feedTitle + " - ";
  	if(feedType == "new")
  	 pageTitle += "новое видео"; 
    else
  	if(feedType == "best")
  	 pageTitle += "популярное видео"; 
  	else
  	if(feedType == "recommend")
  	 pageTitle += "рекоммендуемое видео"; 

    url = "http://www.ivi.ru/rss/"+feedType+feedPath;

    print("Start to load feed:",url);

    dlok = loadXMLFile(url);
    if (dlok != null)
    {
      titleArray = null;
      thumbnailArray = null;
      urlArray = null;

    	itemSize = getXMLElementCount("rss","channel","item");
    	if(itemSize == 0)
    	  postMessage("return");
    	print("Item Size = ", itemSize);

    	count=0;
    	while(itemSize != 0)
    	{
    	  title = getXMLText("rss","channel","item",count,"title");
	      url = getXMLText("rss","channel","item",count,"link");
        thumbnail = getXMLAttribute("rss","channel","item",count,"image","url");

    	  titleArray  = pushBackStringArray(titleArray, title);
    	  urlArray    = pushBackStringArray(urlArray, url);
    	  thumbnailArray = pushBackStringArray(thumbnailArray, thumbnail);

    	  count += 1;
    	  if (count == itemSize)
    	  {
    		  break;
    	  }
    	}
    	setFocusItemIndex(0);
    	redrawDisplay();
    }
  }

submenu.onClick:
titleArray = null;
thumbnailArray = null;
urlArray = null;
idArray = null;
feedType = "new";
setRefreshTime(1);

titleArray = null;
thumbnailArray = null;
urlArray = null;
idArray = null;
feedType = "best";
setRefreshTime(1);

titleArray = null;
thumbnailArray = null;
urlArray = null;
idArray = null;
feedType = "recommend";
setRefreshTime(1);

item_template.onClick:
		  playItemURL(-1, 1);
      idx   = getFocusItemIndex();
      url   = getStringArrayAt(urlArray, idx);
      playTitle = url;
      urlToPlay = translate_base_url+"info,,"+url;
      dlok = loadXMLFile(urlToPlay);
      if(dlok != null)
      {
        error = getXMLElementCount("info","error");
        print("error=",error);

        if(error == 0)
        {


    	    value = getXMLAttribute("info","stream","url");
    	    if(value != null)
    	     url = value;
          playItemURL(url, 0);
  		  }
  		  else
  		  {
  		    null;
  		  }
      }
