rss.script:




max_page = -total_items/-15;
itemCount = getPageInfo("itemCount");
itemSize = null;
nameArray = null;
urlArray = null;
thumbnailArray = null;
executeScript("loadVideosList");

rss.loadVideosList:
data_url = "http://127.0.0.1/cgi-bin/translate?app/rtvv/getVideos,,http://www.rtvv.es/es/mediateca.html?sectionId=9002&type=cap&page=" + cur_page;
dlok = loadXMLFile(data_url);
if (dlok != null)
{
	nItems = getXMLElementCount("data", "video");
	if( nItems > 0 )
	{
		itemSize = nItems;
		nameArray = null;
		urlArray = null;
		thumbnailArray = null;

		count = 0;
		while (count < nItems)
		{
			video_name = getXMLText("data", "video", count, "title");
			video_url = getXMLText("data", "video", count, "url");
			thumb_url = getXMLText("data", "video", count, "image");
			nameArray  = pushBackStringArray(nameArray, video_name);
			urlArray  = pushBackStringArray(urlArray, video_url);
			thumbnailArray = pushBackStringArray(thumbnailArray, thumb_url);
			count += 1;
		}
		setFocusItemIndex(0);
		redrawDisplay();
	}
}

text.script:
if( 1 == cur_page )
	sprintf("%s /", idx-(-1))+total_items;
else
	sprintf("%s /", idx-(-1)-(-15*(cur_page-1)))+total_items;

mediaDisplay.onUserInput:
userInput = currentUserInput();
if( "pagedown" == userInput  && cur_page < max_page )
{
	cur_page -= -1;
	executeScript("loadVideosList");
}
else if( "pageup" == userInput && cur_page > 1 )
{
	cur_page += -1;
	executeScript("loadVideosList");
}
