rss.onEnter:
  translate_base_url  = "http://127.0.0.1/cgi-bin/translate?";
screenXp = 4;
screenYp = 3;
rssconf = readStringFromFile(getStoragePath("key")+"translate.dat");
print("rssconf:",rssconf);
if(rssconf != null)
{
  value = getStringArrayAt(rssconf, 0);
  if(value != null && value != "")
    translate_base_url = value;
  value = getStringArrayAt(rssconf, 1);
  if(value != null && value != "")
    screenXp = Integer(value);
  value = getStringArrayAt(rssconf, 2);
  if(value != null && value != "")
    screenYp = Integer(value);
}
print("rssconf:",translate_base_url,screenXp,screenYp);


  startitem = "";

columnCount=3;
print(getFocusItemIndex());

rss.onRefresh:
setRefreshTime(-1);
itemCount = getPageInfo("itemCount");
middleItem = Integer(itemCount / 2);
if(startitem == "middle")
  setFocusItemIndex(middleItem);
else
if(startitem == "last")
  setFocusItemIndex(itemCount);
else
if(startitem > 0 && startitem <= itemCount)
  setFocusItemIndex(startitem-1);
redrawDisplay();

itemDisplay.script:
				imageW = 90/columnCount;
				imageH = imageW * 3 / 4 * screenXp / screenYp * 100 / 50;
				if(imageH > 86)
				{
				  imageH = 86;
				  imageW = imageH * 4 / 3 * screenYp / screenXp * 50 / imageW;
				}
			  else
			  {
			    imageW = 100;
        }

image.script:
idx = getQueryItemIndex();
focus = getFocusItemIndex();
if(focus==idx) 
{
  annotation = getItemInfo(idx, "annotation");
}
getItemInfo(idx, "image");

widthPC.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) imageW; else imageW * 3 / 4;

heightPC.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) imageH; else imageH * 3 / 4;

offsetXPC.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) 50 - imageW/2; else 50 - imageW * 3 / 8;

offsetYPC.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) 0; else imageH / 8;

fontSize.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) "22"; else "18";

foregroundColor.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) "255:255:255"; else "75:75:75";

onUserInput.script:
ret = "false";
userInput = currentUserInput();
majorContext = getPageInfo("majorContext");

print("*** majorContext=",majorContext);
print("*** userInput=",userInput);

if(userInput == "one" || userInput == "1")
{
  if(itemCount >= 1)
  {
    setFocusItemIndex(0);
    redrawDisplay();
  }
}
else if(userInput == "two" || userInput == "2")
{
  if(itemCount >= 2)
  {
    setFocusItemIndex(1);
    redrawDisplay();
  }
}
else if(userInput == "three" || userInput == "3")
{
  if(itemCount >= 3)
  {
    setFocusItemIndex(2);
    redrawDisplay();
  }
}
else if(userInput == "four" || userInput == "4")
{
  if(itemCount >= 4)
  {
    setFocusItemIndex(3);
    redrawDisplay();
  }
}
else if(userInput == "five" || userInput == "5")
{
  if(itemCount >= 5)
  {
    setFocusItemIndex(4);
    redrawDisplay();
  }
}
else if(userInput == "six" || userInput == "6")
{
  if(itemCount >= 6)
  {
    setFocusItemIndex(5);
    redrawDisplay();
  }
}
else if(userInput == "seven" || userInput == "7")
{
  if(itemCount >= 7)
  {
    setFocusItemIndex(6);
    redrawDisplay();
  }
}
else if(userInput == "eight" || userInput == "8")
{
  if(itemCount >= 8)
  {
    setFocusItemIndex(7);
    redrawDisplay();
  }
}
else if(userInput == "nine" || userInput == "9")
{
  if(itemCount >= 9)
  {
    setFocusItemIndex(8);
    redrawDisplay();
  }
}
else if (userInput == "pagedown" || userInput == "pageup" || userInput == "PD" || userInput == "PG")
{
  itemSize = getPageInfo("itemCount");
  idx = Integer(getFocusItemIndex());
  if (userInput == "pagedown")
  {
    idx -= -columnCount;
    if(idx >= itemSize)
      idx = itemSize-1;
  }
  else
  {
    idx -= columnCount;
    if(idx < 0)
      idx = 0;
  }
  setFocusItemIndex(idx);
  setItemFocus(idx);
  redrawDisplay();
  ret = "true";
}
ret;
