rss.onRefresh:
if(mode == 0)
{
	setRefreshTime(-1);

	dlok = loadXMLFile(translate_base_url+"app/rss/rutube.ru,,http://rutube.ru/cgi-bin/xmlapi.cgi?utf=1&rt_mode=movie&rt_movie_id="+rt_movie_id);
	if (dlok != null)
	{
		pageTitle    = getXMLText("response","movie","title");
		description  = getXMLText("response","movie","description");
		author       = getXMLText("response","movie","author");
		size         = getXMLText("response","movie","size");
		duration     = getXMLText("response","movie","duration");
		date         = getXMLText("response","movie","recordDate");
		category     = getXMLText("response","movie","categoryName");
		thumbnail    = getXMLText("response","movie","thumbnailLink");
		url          = getXMLText("response","movie","playerLink");
		redrawDisplay();
	}
}
else if(mode == 1)
{
	print("Wait for movie stop");
	vidProgress = getPlaybackStatus();
	playStatus = getStringArrayAt(vidProgress, 3);
	if(playStatus == 0)
	{
		cancelIdle();
		setRefreshTime(-1);
		mode = 0;
	}
}

rss.onEnter:
translate_base_url  = "http://127.0.0.1/cgi-bin/translate?";
screenXp = 4;
screenYp = 3;
rssconf = readStringFromFile(getStoragePath("key")+"translate.dat");
print("rssconf:",rssconf);
if(rssconf != null)
{
	value = getStringArrayAt(rssconf, 0);
	if(value != null && value != "")
		translate_base_url = value;
	value = getStringArrayAt(rssconf, 1);
	if(value != null && value != "")
		screenXp = Integer(value);
	value = getStringArrayAt(rssconf, 2);
	if(value != null && value != "")
		screenYp = Integer(value);
}
print("rssconf:",translate_base_url,screenXp,screenYp);

cachePath = getStoragePath("key");

storagePath = getStoragePath("tmp");
storagePath = storagePath + "rutube.dat";
arr = readStringFromFile(storagePath);
pageTitle   = getStringArrayAt(arr, 1);
rt_page     = getStringArrayAt(arr, 2);
sort_by     = getStringArrayAt(arr, 3);
rt_term     = getStringArrayAt(arr, 4);
rt_count    = getStringArrayAt(arr, 5);
rt_movie_id = getStringArrayAt(arr, 6);

setReturnString("");

pageLogo = "../etc/translate/rss/image/rutube.png";

mode = 0;
setRefreshTime(1);

submenu.onClick:
mode = 1;

playItemURL(-1, 1);
urlToPlay = translate_base_url+"info,,"+url;
dlok = loadXMLFile(urlToPlay);
if(dlok != null)
{
	error = getXMLElementCount("info","error");
	print("error=",error);

	if(error == 0)
	{
		historyPath = getStoragePath("key")+"translate.history_video.m3u";
		historydat = readStringFromFile(historyPath);
		if(historydat == null)
		historydat = pushBackStringArray(historydat, "#EXTM3U");
		historydat = pushBackStringArray(historydat, "#EXTINF:-1,"+pageTitle);
		historydat = pushBackStringArray(historydat, url);
		writeStringToFile(historyPath, historydat);

		value = getXMLAttribute("info","stream","url");
		if(value != null)
		{
			showIdle();
			setRefreshTime(1000);
			playItemURL(translate_base_url+"stream,,"+value, 0);
			cancelIdle();
		}
	}
}
null;

			arr = null;
			arr = pushBackStringArray(arr, translate_base_url+"app/rss/rutube.ru,http://rutube.ru/cgi-bin/xmlapi.cgi?utf=1&rt_mode=related&rt_movie_id="+rt_movie_id);
			arr = pushBackStringArray(arr, "Vídeos relacionados: "+pageTitle);
			arr = pushBackStringArray(arr, rt_page);
			arr = pushBackStringArray(arr, sort_by);
			arr = pushBackStringArray(arr, rt_term);
			arr = pushBackStringArray(arr, rt_count);
			print("arr=",arr);
			print("storagePath=",storagePath);
			writeStringToFile(storagePath,arr);

			setReturnString("related");
			postMessage("return");
/*
			doModalRss("rss_file://../etc/translate/rss/rutube/rutube_movies.rss");
*/
			null;
