image.script:
idx1 = getFocusItemIndex();
idx2 = getQueryItemIndex();
if (idx1 == idx2)
	"../etc/translate/rss/image/IMAGE_NEWRSS_MENU_BTFOC.bmp";
else
	"";

onUserInput.script:
				ret = "false";

				userInput = currentUserInput();
				majorContext = getPageInfo("majorContext");

				print("*** majorContext=",majorContext);
				print("*** userInput=",userInput);

				idx = Integer(getFocusItemIndex());

				if (majorContext == "items" && idx < 3)
				{
					if( userInput == "left" )
					{
						if(idx == 0)
						{
							if(regionIndex == 0)
								regionIndex = regionCount - 1;
							else
								regionIndex = regionIndex - 1;
						}
						else if(idx == 1)
							hd = 1 - hd;
          					ret = "true";
						redrawDisplay();
					}
					else if( userInput == "right" || userInput == "enter" )
					{
						if(idx == 0)
						{
							regionIndex = regionIndex + 1;
							if(regionIndex == regionCount)
								regionIndex = 0;
						}
						else if(idx == 1)
							hd = 1 - hd;
						else
						{
							print("username!!!!");
							keyword = getInput();
							if (keyword != null)
								username = keyword;
						}
						ret = "true";
						redrawDisplay();
					}
				}
				ret;

rss.onExit:
		arr = null;

		if(username == "" || username == null)
			username = " ";

		youtubeRegion = getStringArrayAt(regionSymbolArray, regionIndex);

		arr = pushBackStringArray(arr, youtubeRegion);
		arr = pushBackStringArray(arr, hd);
		arr = pushBackStringArray(arr, username);

		print("arr=",arr);

		writeStringToFile(optionsPath, arr);

		optionsArray = readStringFromFile(getStoragePath("key")+"translate.dat");

		arr = null;

		arr = pushBackStringArray(arr, getStringArrayAt(optionsArray, 0));
		arr = pushBackStringArray(arr, getStringArrayAt(optionsArray, 1));
		arr = pushBackStringArray(arr, getStringArrayAt(optionsArray, 2));
		arr = pushBackStringArray(arr, hd);
		arr = pushBackStringArray(arr, getStringArrayAt(optionsArray, 4));
		arr = pushBackStringArray(arr, getStringArrayAt(optionsArray, 5));
		arr = pushBackStringArray(arr, getStringArrayAt(optionsArray, 6));
		arr = pushBackStringArray(arr, getStringArrayAt(optionsArray, 7));

		writeStringToFile(getStoragePath("key")+"translate.dat", arr);

rss.onEnter:
		translate_base_url  = "http://127.0.0.1/cgi-bin/translate?";
		screenXp = 4;
		screenYp = 3;
		rssconf = readStringFromFile(getStoragePath("key")+"translate.dat");
		print("rssconf:",rssconf);
		if(rssconf != null)
		{
			value = getStringArrayAt(rssconf, 0);
			if(value != null && value != "")
				translate_base_url = value;
			value = getStringArrayAt(rssconf, 1);
			if(value != null && value != "")
				screenXp = Integer(value);
			value = getStringArrayAt(rssconf, 2);
			if(value != null && value != "")
				screenYp = Integer(value);
		}
		print("rssconf:",translate_base_url,screenXp,screenYp);

		pageTitle = "Configuración";
		cachePath = getStoragePath("key");
		regionPath = cachePath + "youtubeRegion.dat";
		youtubeRegion = readStringFromFile(regionPath);
		if(youtubeRegion == null)
			youtubeRegion = "ES";
		print(youtubeRegion);

		optionsPath = cachePath + "youtubeOptions.dat";
		optionsArray = readStringFromFile(optionsPath);
		if(optionsArray == null)
		{
			hd = 0;
			username = " ";
		}
		else
		{
			youtubeRegion = getStringArrayAt(optionsArray, 0);
			hd = getStringArrayAt(optionsArray, 1);
			username = getStringArrayAt(optionsArray, 2);
		}

		regionArray = null;
		regionSymbolArray = null;

		regionCount = 20;

		regionArray = pushBackStringArray(regionArray, "Mundial");
		regionArray = pushBackStringArray(regionArray, "Australia");
		regionArray = pushBackStringArray(regionArray, "Brasil");
		regionArray = pushBackStringArray(regionArray, "Reino Unido");
		regionArray = pushBackStringArray(regionArray, "Alemania");
		regionArray = pushBackStringArray(regionArray, "Países Bajos");
		regionArray = pushBackStringArray(regionArray, "Hong Kong");
		regionArray = pushBackStringArray(regionArray, "Irlanda");
		regionArray = pushBackStringArray(regionArray, "España");
		regionArray = pushBackStringArray(regionArray, "Italia");
		regionArray = pushBackStringArray(regionArray, "Canadá");
		regionArray = pushBackStringArray(regionArray, "México");
		regionArray = pushBackStringArray(regionArray, "Nueva Zelanda");
		regionArray = pushBackStringArray(regionArray, "Polonia");
		regionArray = pushBackStringArray(regionArray, "Rusia");
		regionArray = pushBackStringArray(regionArray, "USA");
		regionArray = pushBackStringArray(regionArray, "Taiwán");
		regionArray = pushBackStringArray(regionArray, "Francia");
		regionArray = pushBackStringArray(regionArray, "Corea del Sur");
		regionArray = pushBackStringArray(regionArray, "Japón");

		regionSymbolArray = pushBackStringArray(regionSymbolArray, " ");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "AU");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "BR");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "GB");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "DE");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "NL");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "HK");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "IE");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "ES");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "IT");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "CA");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "MX");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "NZ");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "PL");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "RU");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "US");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "TW");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "FR");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "KR");
		regionSymbolArray = pushBackStringArray(regionSymbolArray, "JP");

		regionIndex = 0;

		while(1)
		{
			s = getStringArrayAt(regionSymbolArray, regionIndex);
			if(s == youtubeRegion)
				break;

			regionIndex = regionIndex + 1;
			if(regionIndex == regionCount)
			{
				regionIndex = 0;
				break;
			}
		}

		itemTitleArray = null;
		topUrl="../etc/translate/rss/youtube/yt_options.rss";
		dlok = loadXMLFile(topUrl);
		if (dlok != null)
		{
			itemSize = getXMLElementCount("rss", "channel", "item");
			print("itemSize = ", itemSize);

			if(itemSize > 0)
			{
				itemCount = 0;

				while(1)
				{
					itemTitle  = getXMLText("rss", "channel", "item", itemCount, "title");
					print("itemTitle = ", itemTitle);
					itemTitleArray  = pushBackStringArray(itemTitleArray, itemTitle);

					itemCount += 1;
					if(itemCount == itemSize)
						break;
				}
				setFocusItemIndex(0);
				setItemFocus(0);
				redrawDisplay();
			}
		}
